mapper.update:
        update t_medicine 
        set med_name=#{med_name}, med_alarm=#{med_alarm}
		where user_id=#{user_id} and med_box=#{med_box}

    update t_medicine 
    set med_name=#{med_name}, med_hosp=#{med_hosp}, med_way=#{med_way}, med_times=#{med_times}, 
    	med_date=#{med_date}, med_alarm=#{med_alarm}, med_memo=#{med_memo}
	where user_id=#{user_id} and med_box=#{med_box}
